import React from 'react'
import { Col, Container, Row } from 'react-bootstrap'
import '../App.css'
import axios from 'axios'
import {useEffect, useState} from 'react'
import {useParams} from 'react-router'


const ThirdScreen = ({match}) => {

    const url = useParams()
    const [Detail, setDetail] = useState([])

    useEffect(() => {
       const fechData = async() =>{
           try {
            const {data} = await axios.get(`/${url.name}`) 
            const a = Object.keys(data[url.titleId])
            const vue = data[url.titleId]
            setDetail(vue[`${a[5]}`])
           } catch (error) {
               console.log(error)
           }
       }
       fechData()
    }, [])
   
    //find specific destination

    const countryDetail = Detail.find((d)=>d._id === match.params.id )
    console.log(countryDetail.name)
    

    return (
        <Container className="third-screen">
            <div className="third-screen-img my-2">
                <img src="/image/goa.jpg"/>
            </div>
            <div className="third-screen-title">
                <h1> {countryDetail.name} - {countryDetail.state}</h1>
            </div>
            <div className="third-screen-content">
                <p> {countryDetail.description} </p>
            </div>
            <div className="third-screen-element">
                <Row className="w-100">
                    <Col>
                        <div className="third-screen-col1 my-3" >
                                    dafaf
                        </div>
                    </Col>
                    <Col>
                        <div className="third-screen-col2 my-3">
                                  vhda
                        </div>
                    </Col>
                    <Col>
                        <div className="third-screen-col3 my-3">
                                    scsys
                        </div>
                    </Col>
                </Row>
            </div>

        </Container>
    )
}

export default ThirdScreen
