import React from 'react'
import { Col, Container, Row } from 'react-bootstrap'
import '../App.css'
import axios from 'axios'
import {useEffect, useState} from 'react'
import {useParams} from 'react-router'
import Datetime from 'react-datetime'
import Clock from 'react-live-clock';
import {Link} from 'react-router-dom'


const ThirdScreen = ({match}) => {

///////////////////////////////////////////////////////////
    const url = useParams()
    console.log(url.name,url.title,url.titleId,url.id)
    const [Detail, setDetail] = useState([])
    let countryDetail = null
    useEffect(() => {
        const fechData = async() =>{
            try {
             const {data} = await axios.get(`/${url.name}`) 
             const a = Object.keys(data[url.titleId])
             var vue = data[url.titleId]
             countryDetail = vue[`${a[5]}`].find((d)=>d._id === match.params.id )
             setDetail(countryDetail)
            } catch (error) {
                console.log(error)
            }
        }
        fechData()
     }, [])
     console.log(Detail.name)
//////////////////////////////////////////////


////////////////<WEATHER>////////////////////

const [weather, setWeather] = useState('')

useEffect(()=>{
    const weatherInfo = async() =>{
        try {
            const {data} = await axios.get('https://api.openweathermap.org/data/2.5/weather?q=singrauli&units=metric&appid=b0ae0985edaf0e0b11f5fa2866865c79')
            setWeather(data)
            console.log(data.main.temp)
        } catch (error) {
            console.log(error)
        }
        
    }
    weatherInfo()
},[])

console.log(weather)

//////////////////////////////////////////////////






     
    return (
    <div className="tt">
        <Container className="third-screen">
            <div className="third-screen-img my-2">
                <img src={Detail.imagesrc} alt="taj"/>
            </div>
            <div className="third-screen-title">
                <h1>{Detail.name}</h1>
            </div>
            <div className="third-screen-content">
                <p>{Detail.description} </p>
            </div>
            <div className="third-screen-element">
                <Row className="my-4">
                    <Col className="third-screen-colone my-3">
                        {!weather ? (
                            <>
                           <h1>404</h1>
                           <span>page not found</span>
                           </>
                        ):(
                            <div>
                                   <div className="third-screen-col1 my-3" >
                                          <img src="/image/cloudy.png" />
                                    </div>
                                    <div className="text-center third-screen-colone-txt my-2">
                                        <h1><span style={{color:'red'}} >temp -</span>&nbsp;<span style={{color:'black'}} >{weather.main.temp}&#176;C</span></h1>
                                        <span>{<Clock format={'HH:mm:ss'} ticking={true} timezone={'US/Pacific'} />}</span><br/>
                                        <span>feels Like - {Math.floor(weather.main.feels_like)}&#176;C</span><br/>
                                        <span>Pressure - { Math.floor(weather.main.temp*3.6) }Km/h</span><br/>
                                        <span>Wind Speed - {Math.floor( weather.wind.speed*3.6)} Km/h</span><br/>
                                        <span>Wind Gusts - {Math.floor(weather.wind.gust*3.6)} Km/h</span><br/>
                                        <Link to ={`/${url.name}/${url.title}/${url.titleId}/${url.details}/${url.id}/weather`}>Know More</Link>
                                    </div>   
                            </div>
                        )}
                      
                    </Col>
                   
                </Row>
            </div>


            <div className="third-screen-nav">
                <div className="row">
                    <div className="col third-screen-nav-colone">
                        <div>
                            location
                        </div>
                    </div>
                    <div className="col third-screen-nav-colone">
                        <div>
                             nearhotel
                        </div>
                    </div>
                    <div className="col third-screen-nav-colone">
                        <div>
                             nearhotel
                        </div>
                    </div>
                    <div className="col third-screen-nav-colone">
                            weather
                    </div>

                </div>
            </div>

        </Container>
        </div>
    )
}
////////////////////////////////////////////////////////////////:name/:title/:titleId/:details/:id/:weather
export default ThirdScreen
