import React from 'react'
import { Col,Row, Container } from 'react-bootstrap'
import '../App.css'
import {LinkContainer} from 'react-router-bootstrap'
import { useParams } from 'react-router'
import {Link} from 'react-router-dom'

import { Card } from 'react-bootstrap';

const SecondScreenCard = ({secondscreen}) => {
  const url = useParams()
  return (
    <div className="ss-card">
    <Card className="text-white s-screen-card my-4 text-center">
         <Card.Img src={secondscreen.imagesrc} alt="Card image" />
      
   
<Card.ImgOverlay>
    <Card.Title className="ss-card-title">{secondscreen.name}</Card.Title>
    <Link className="ss-card-link" to={`/${url.name}/${url.title}/${url.titleId}/${secondscreen.name}/${secondscreen._id}`}>Learn more</Link>
    <Card.Text className="ss-card-text">{url.name}</Card.Text>
    
</Card.ImgOverlay>
</Card>
</div>
  );
};

export default SecondScreenCard;
