import React from 'react'
import { Col, Container, Row } from 'react-bootstrap'
import {LinkContainer} from 'react-router-bootstrap'
import '../App.css'
import axios from 'axios'
import {useEffect, useState} from 'react'
import {useParams} from 'react-router'
import Clock from 'react-live-clock';
import {Link} from 'react-router-dom'
import LocationOnIcon from '@material-ui/icons/LocationOn';
import HotelIcon from '@material-ui/icons/Hotel';
import CloudSharpIcon from '@material-ui/icons/CloudSharp';
import FlightTakeoffSharpIcon from '@material-ui/icons/FlightTakeoffSharp';


const ThirdScreen = ({match}) => {

///////////////////////////////////////////////////////////
    const url = useParams()
    console.log(url.name,url.title,url.titleId,url.id)
    const [Detail, setDetail] = useState([])
    let countryDetail = null
    useEffect(() => {
        const fechData = async() =>{
            try {
             const {data} = await axios.get(`/${url.name}`) 
             const a = Object.keys(data[url.titleId])
             var vue = data[url.titleId]
             countryDetail = vue[`${a[5]}`].find((d)=>d._id === match.params.id )
             setDetail(countryDetail)
            } catch (error) {
                console.log(error)
            }
        }
        fechData()
     }, [])
     console.log(Detail.name)
//////////////////////////////////////////////


////////////////<WEATHER>////////////////////

const [weather, setWeather] = useState('')

useEffect(()=>{
    const weatherInfo = async() =>{
        try {
            const {data} = await axios.get('https://api.openweathermap.org/data/2.5/weather?q=singrauli&units=metric&appid=b0ae0985edaf0e0b11f5fa2866865c79')
            setWeather(data)
            console.log(data.main.temp)
        } catch (error) {
            console.log(error)
        }
        
    }
    weatherInfo()
},[])

console.log(weather)

//////////////////////////////////////////////////






     
    return (
