import React,{useState,useEffect} from 'react'
import {Row, Col} from 'react-bootstrap'
import HomePlace from '../Places/HomePlace'
import HomeScreenCard from './HomeScreenCard'
import India from '../Places/India'
import { useParams } from 'react-router'
import USA from '../Places/USA'
import axios from 'axios'


const [Country, setCountry] = useState([])

useEffect(() => {
    const countryData = async() => {
       const {data} = await axios.get('/india')
       setCountry(data)
    }
    countryData()
}, [])






        




const HomeScreenCardItem = ({match}) => {
      const CountryId = useParams()
    if (!CountryId.id) {
        return (
          
            <>
           
                <Row className="home-screen-container my-3 p-3 w-100" >
                    {
                       India.map((homeplace)=>(
                            <Col>
                                <HomeScreenCard
                                    homeplace={homeplace}
                                />
                            </Col>
                        ))
                    }
                </Row>
            </>
        )
    } else {
        return (
        
            <>
           
                <Row className="home-screen-container my-3 p-3 w-100" >
                    {
                       Country[CountryId.id].map((homeplace)=>(
                            <Col>
                                <HomeScreenCard
                                    homeplace={homeplace}
                                />
                            </Col>
                        ))
                    }
                </Row>
            </>
        )
    }
   
}

export default HomeScreenCardItem
