import React from 'react'
import { Col, Container, Row } from 'react-bootstrap'
import '../App.css'
import axios from 'axios'
import {useEffect, useState} from 'react'
import {useParams} from 'react-router'
import { get } from 'mongoose'


const ThirdScreen = ({match}) => {

///////////////////////////////////////////////////////////
    const url = useParams()
    console.log(url.name,url.title,url.titleId,url.id)
    const [Detail, setDetail] = useState([])
    let countryDetail = null
    useEffect(() => {
        const fechData = async() =>{
            try {
             const {data} = await axios.get(`/${url.name}`) 
             const a = Object.keys(data[url.titleId])
             var vue = data[url.titleId]
             countryDetail = vue[`${a[5]}`].find((d)=>d._id === match.params.id )
             setDetail(countryDetail)
            } catch (error) {
                console.log(error)
            }
        }
        fechData()
     }, [])
     console.log(Detail.name)
////////////////<WEATHER>////////////////////
const [weather, setWeather] = useState('')
const Weather = async() =>{
    try {
        const res = await axios.get('http://api.openweathermap.org/data/2.5/weather?q=bhopal&appid=b0ae0985edaf0e0b11f5fa2866865c79')
        setWeather(res)
    } catch (error) {
        console.log(error)
    }
    
}
console.log(weather)
//////////////////////////////////////////////////



//////////////////////////////////////////////////


     
    return (
        <Container className="third-screen">
            <div className="third-screen-img my-2">
                <img src={Detail.imagesrc} alt="taj"/>
            </div>
            <div className="third-screen-title">
                <h1>{Detail.name}</h1>
            </div>
            <div className="third-screen-content">
                <p>{Detail.description} </p>
            </div>
            <div className="third-screen-element">
                <Row className="w-100 my-4">
                    <Col className="third-screen-colone">
                        <div className="third-screen-col1 my-3" >
                                <img src="/image/cloudy.png" />
                        </div>
                        <div className="text-center third-screen-colone-txt my-2">
                            <h1>temp 0.C</h1>
                            <span>time 09:45:00</span><br/>
                            <span>Claud</span><br/>
                            <span>Air Quality - poor</span><br/>
                            <span>Wind - 12.45HZ</span><br/>
                            <span>Wind Gusts - 6km/h</span><br/>
                            <a href="">Know more....</a>
                        </div>   
                    </Col>
                    <Col>
                        <div className="third-screen-col2 my-3">
                                  vhda
                        </div>
                    </Col>
                    <Col>
                        <div className="third-screen-col3 my-3">
                                    scsys
                        </div>
                    </Col>
                </Row>
            </div>

        </Container>
    )
}

export default ThirdScreen
