import React,{useState, useEffect} from 'react'
import { Container } from 'react-bootstrap'
import {Row, Col, } from 'react-bootstrap'
import SecondScreenCard from './SecondScreenCard'
import India from '../Places/India'
import USA from '../Places/USA'
import { useParams } from 'react-router'
import axios from 'axios'


const SecondScreenitem = () => {

    const url = useParams()
    const urlTitle = url.title
    const [Subplace, setSubplace] = useState([])

    useEffect(() => {
       const fechData = async() =>{
           try {
            const {data} = await axios.get(`/${url.name}`) 
            setSubplace(data[url.titleId].adventure)
            const myData=JSON.parse(data[url.titleId])
            let aa = myData
           } catch (error) {
               console.log(error)
           }
       }
       fechData()
    }, [])
    console.log(url.titleId, urlTitle)
    console.log(Subplace)
   
    return (
        <>
        <Container>
           <Row className="home-screen-container my-3 p-3 w-100" >
                {
                Subplace.map((secondscreen)=>(
                        <Col>
                            <SecondScreenCard
                                secondscreen={secondscreen}
                            />
                        </Col>
                    ))
                }
            </Row>
        </Container>
        </>
    )
}

export default SecondScreenitem
