import React from 'react'
import { Col,Row, Container, Button } from 'react-bootstrap'
import '../App.css'
import {LinkContainer} from 'react-router-bootstrap'
import { useParams } from 'react-router'
import {Link} from 'react-router-dom'

import { Card } from 'react-bootstrap';

const SecondScreenCard = ({secondscreen}) => {
  const url = useParams()
  return (
    
    <Card className="text-white s-screen-card my-4 text-center " style={{backgroundColor:'#585657'}}>
         <Card.Img src={secondscreen.imagesrc} alt="Card image" />  
         <Card.ImgOverlay>
            <Card.Title style={{fontSize:'30px'}} className="ss-card-title">{secondscreen.name}</Card.Title>
            <LinkContainer to={`/${url.name}/${url.title}/${url.titleId}/${secondscreen.name}/${secondscreen._id}`} >
               <button className="ss-card-button">About More</button>
            </LinkContainer>
         </Card.ImgOverlay>
    </Card>

  );
};

export default SecondScreenCard;
