import React, {useState} from 'react';
import {TextField} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {Button, Container, Col, Row} from 'react-bootstrap'
import '../App.css'
import {Link} from 'react-router-dom'

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
}));

export default function SignUp() {
  const classes = useStyles();

  const [Name, setName] = useState('')
  const [Email, setEmail] = useState('')
  const [Mobile, setMobile] = useState('')
  const [Password, setPassword]= useState('')
  const [ConfirmPassword, setConfirmPassword] = useState('')

  const userResister = async()=>{
    const res = await fetch('/register',{
      method:'POST',
      headers:{
        "Content-Type":"application/json"
      },
      body:JSON.stringify({
        Name, Email, Mobile, Password, 
      })
    })
  }
  
  return (
   
        <section className="singup">
            <div className="singup-content">
            <Row>
                <Col>
                    <div style={{ width:'260px',margin:'auto'}}>
                        <div className="signUp text-center">
                           <span>Sign Up</span>
                        </div>
                        <form  method="POST" className={classes.root} noValidate autoComplete="off">
                            <div>
                                <TextField required id="standard-required" label="username" type="text"  onChange={(e)=>{setName(e.target.value)}} /><br/>
                                <TextField required id="standard-required" label="Emai" type="email"  onChange={(e)=>{setEmail(e.target.value)}} /><br/>
                                <TextField id="standard-number" label="Mobile Number"  type="number"  onChange={(e)=>{setMobile(e.target.value)}}/><br/>
                                <TextField id="standard-password-input" label="Password" type="password"  autoComplete="current-password"  onChange={(e)=>{setPassword(e.target.value)}}/><br/>
                                <TextField id="standard-password-input" label="Confirm Password" type="password"  autoComplete="Confirm-password"  onChange={(e)=>{setConfirmPassword(e.target.value)}}  /><br/>
                                <Button variant="success" type="submit" onClick={userResister} >Register</Button>
                            </div>
                        </form>
                            <div className="text-center mt-2">
                               <Link className="singup-content-link" to='/user/signIn'>already register | Login</Link>
                            </div>    
                    </div>
                </Col>
               
            </Row>
            </div>
            {console.log(Name,Email,Password,Mobile,ConfirmPassword)}
        </section>
        
    
  );
}
